    private void btnSumaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSumaActionPerformed
        // TODO add your handling code here:
        int numero1 = 0;
        int numero2 = 0;
        int suma = 0;
        
        //Esta es la convercion a numero de texto:
        //integer.parseInt(this.texto1.getText());
        
        numero1 = Integer.parseInt(this.txt1.getText());
        numero2 = Integer.parseInt(this.txt2.getText());
        
        suma = numero1 + numero2;
      
        //Esto comvierte un dato numerico a cadena:
        //setText()-->asignacion
        jlSuma.setText(String.valueOf(suma));
    }//GEN-LAST:event_btnSumaActionPerformed

