    private void btnRestaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRestaActionPerformed
        // TODO add your handling code here:
        int numero1 = 0;
        int numero2 = 0;
        int resta = 0;
        
        //Esta es la convercion a numero de texto:
        //integer.parseInt(this.texto1.getText());
        
        numero1 = Integer.parseInt(this.txt3.getText());
        numero2 = Integer.parseInt(this.txt4.getText());
        
        resta = numero1 - numero2;
      
        //Esto comvierte un dato numerico a cadena:
        //setText()-->asignacion
        jlResta.setText(String.valueOf(resta));
    }//GEN-LAST:event_btnRestaActionPerformed

    private void btnProductoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnProductoActionPerformed
        // TODO add your handling code here:
        int numero1 = 0;
        int numero2 = 0;
        int producto = 0;
        
        //Esta es la convercion a numero de texto:
        //integer.parseInt(this.texto1.getText());
        
        numero1 = Integer.parseInt(this.txt5.getText());
        numero2 = Integer.parseInt(this.txt6.getText());
        
        producto = numero1*numero2;
      
        //Esto comvierte un dato numerico a cadena:
        //setText()-->asignacion
        jlProducto.setText(String.valueOf(producto));
    }//GEN-LAST:event_btnProductoActionPerformed

    private void btnDivicionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnDivicionActionPerformed
        // TODO add your handling code here:
        int numero1 = 0;
        int numero2 = 0;
        int divicion = 0;
        
        //Esta es la convercion a numero de texto:
        //integer.parseInt(this.texto1.getText());
        
        numero1 = Integer.parseInt(this.txt7.getText());
        numero2 = Integer.parseInt(this.txt8.getText());
        
        if (numero2 == 0) {
           jlDivicion.setText("Error!");
        } else {
            divicion = numero1 / numero2;
            jlDivicion.setText(String.valueOf(divicion));
        }
      
        //Esto comvierte un dato numerico a cadena:
        //setText()-->asignacion
       
    }//GEN-LAST:event_btnDivicionActionPerformed

